mapper.select:
select , qiio.incoming_inspection_order_code,qiio.mrb_result,qiio.order_qty,qiio.remark, bm.material_code,bm.material_desc,bm.material_version, bpm.product_model_name,bs.supplier_name, su.user_name as operator_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from qms_badness_manage qbm left join qms_incoming_inspection_order qiio on qbm.incoming_inspection_order_id=qiio.incoming_inspection_order_id left join base_material bm on qiio.material_id=bm.material_id left join base_material_tab bmt on bm.material_id=bmt.material_id left join base_product_model bpm on bmt.product_model_id=bpm.product_model_id left join base_supplier bs on qiio.supplier_id=bs.supplier_id left join sys_user su on qbm.operator_user_id=su.user_id left join sys_user u on qbm.create_user_id=u.user_id left join sys_user s on qbm.modified_user_id=s.user_id left join base_organization bo ON qbm.org_id = bo.organization_id
and qiio.incoming_inspection_order_code like CONCAT('%', #{incomingInspectionOrderCode}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and bs.supplier_name like CONCAT('%', #{supplierName}, '%')
and qbm.status = #{status}
and date_format(qbm.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and qbm.org_id=#{orgId}
order by qbm.create_time desc
