mapper.select:
select , mpwo.work_order_code, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.qms_ht_ipqc_inspection_order qhiio left join fantech_imes_v2.mes_pm_work_order mpwo on qhiio.work_order_id=mpwo.work_order_id left join fantech_imes_v2.mes_sfc_work_order_barcode mswob on qhiio.work_order_barcode_id=mswob.work_order_barcode_id left join fantech_imes_v2.base_process_inspection_item bpii on qhiio.process_inspection_item_id=bpii.process_inspection_item_id left join fantech_imes_v2.base_dept bd on qhiio.dept_id=bd.dept_id left join ocean_v2.sys_user su on qhiio.approve_user_id=su.user_id left join ocean_v2.sys_user u on qhiio.create_user_id=u.user_id left join ocean_v2.sys_user s on qhiio.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON qhiio.org_id = bo.organization_id
and qhiio.ipqc_inspection_order_code like CONCAT('%', #{ipqcInspectionOrderCode}, '%')
and qhiio.status = #{status}
and date_format(qhiio.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by qhiio.create_time desc
