mapper.select:
select * from ( select , bm.material_code,bm.material_name,bm.material_desc,bm.material_version, bmt.main_unit,bpm.product_model_name, bs.supplier_name,bw.warehouse_name,biw.inspection_way_desc,bis.inspection_standard_name, bf.access_url, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name, sum(wimb.material_qty) as total_material_qty from qms_incoming_inspection_order qiio left join base_material bm on qiio.material_id=bm.material_id left join base_material_tab bmt on bm.material_id=bmt.material_id left join base_product_model bpm on bmt.product_model_id=bpm.product_model_id left join base_supplier bs on qiio.supplier_id=bs.supplier_id left join base_warehouse bw on qiio.warehouse_id=bw.warehouse_id left join base_inspection_standard bis on qiio.inspection_standard_id=bis.inspection_standard_id left join base_inspection_way biw on qiio.inspection_way_id=biw.inspection_way_id left join base_file bf on qiio.file_id=bf.file_id left join sys_user u on qiio.create_user_id=u.user_id left join sys_user s on qiio.modified_user_id=s.user_id left join base_organization bo ON qiio.org_id = bo.organization_id left join wms_inner_material_barcode_re_order wimbro on wimbro.order_code = qiio.incoming_inspection_order_code left join wms_inner_material_barcode wimb on wimb.material_barcode_id = wimbro.material_barcode_id and wimb.barcode_type = 1
and qiio.incoming_inspection_order_id = #{incomingInspectionOrderId}
and qiio.incoming_inspection_order_code like CONCAT('%', #{incomingInspectionOrderCode}, '%')
and qiio.incoming_inspection_order_code = #{incomingInspectionOrderCode}
and qiio.source_order_code like CONCAT('%', #{sourceOrderCode}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and bm.material_name like CONCAT('%', #{materialName}, '%')
and bm.material_desc like CONCAT('%', #{materialDesc}, '%')
and bm.material_version like CONCAT('%', #{materialVersion}, '%')
and qiio.inspection_status = #{inspectionStatus}
and qiio.inspection_status
#{item}
and qiio.inspection_result = #{inspectionResult}
and qiio.mrb_result = #{mrbResult}
and qiio.status = #{status}
and date_format(qiio.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and qiio.org_id=#{orgId}
and qiio.supplier_id=#{supplierId}
GROUP BY qiio.incoming_inspection_order_id order by qiio.create_time desc ) d
(d.total_material_qty < d.order_qty || d.total_material_qty is null)
and #FULL_FROM_QUERY

update.foreach:
update qms_incoming_inspection_order qiio set
qiio.if_all_issued = #{item.ifAllIssued}
where qiio.incoming_inspection_order_id = #{item.incomingInspectionOrderId}
