mapper.select:
select , bm.material_code,bm.material_desc,bm.material_version, bs.supplier_name as customer_name, bis.inspection_standard_name, biw.inspection_way_code, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.qms_inspection_order qio left join fantech_imes_v2.base_material bm on qio.material_id=bm.material_id left join fantech_imes_v2.base_supplier bs on qio.customer_id=bs.supplier_id left join fantech_imes_v2.base_inspection_standard bis on qio.inspection_standard_id=bis.inspection_standard_id left join fantech_imes_v2.base_inspection_way biw on qio.inspection_way_id=biw.inspection_way_id left join ocean_v2.sys_user u on qio.create_user_id=u.user_id left join ocean_v2.sys_user s on qio.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON qio.org_id = bo.organization_id
and qio.inspection_order_id = #{inspectionOrderId}
and qio.inspection_order_code like CONCAT('%', #{inspectionOrderCode}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and qio.inspection_status = #{inspectionStatus}
and qio.inspection_status
#{item}
and qio.inspection_result = #{inspectionResult}
and qio.status = #{status}
and date_format(qio.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by qio.create_time desc
