mapper.select:
select ,su.user_name AS create_user_name, sus.user_name AS modified_user_name,bo.organization_name, sm.material_code,sm.material_desc,spm.product_model_name,mpm.bar_code AS pallet_code, bmpm.bar_code AS box_code,bmpm.total AS total_quantity,bt.main_unit AS unit,1 AS checkout_type, swa.warehouse_area_name,qpid.inspection_quantity - qpid.qualified_quantity AS unqualified_quantity from fantech_imes.qms_pda_inspection_det qpid LEFT JOIN ocean.sys_user su ON qpid.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON qpid.modified_user_id = sus.user_id LEFT JOIN fantech_imes.base_organization bo ON qpid.organization_id = bo.organization_id LEFT JOIN fantech_imes.qms_pda_inspection qpi ON qpid.pda_inspection_id = qpi.pda_inspection_id LEFT JOIN fantech_imes.mes_package_manager mpm ON qpi.package_manager_id = mpm.package_manager_id LEFT JOIN fantech_imes.mes_package_manager bmpm ON qpid.package_manager_id = bmpm.package_manager_id LEFT JOIN fantech_imes.smt_work_order swo ON swo.work_order_id = mpm.work_order_id LEFT JOIN fantech_imes.smt_material sm ON sm.material_id = swo.material_id LEFT JOIN fantech_imes.base_tab bt ON sm.material_id = bt.material_id LEFT JOIN fantech_imes.smt_product_model spm ON spm.product_model_id = bt.product_model_id
LEFT JOIN fantech_imes.qms_andin_storage_quarantine qasq ON qasq.pallet_id = qpi.package_manager_id
LEFT JOIN smt_warehouse_area swa on swa.warehouse_area_id = qasq.inspection_waiting_area_id
and qpid.pda_inspection_id = #{pdaInspectionId}
and qpid.package_manager_id = #{packageManagerId}
and date_format(qpid.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by create_time desc
