mapper.select:
select ,su.user_name AS create_user_name, sus.user_name AS modified_user_name,bo.organization_name, swo.work_order_code,swocp.work_order_card_id,sm.material_desc,sm.material_code,spm.product_model_name, bt.main_unit AS unit,bpi.parts_information_name,sd.dept_name,su.user_name AS personName, sp.process_name,sws.section_name from fantech_imes.qms_quality_confirmation qqc LEFT JOIN ocean.sys_user su ON qqc.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON qqc.modified_user_id = sus.user_id LEFT JOIN fantech_imes.base_organization bo ON qqc.organization_id = bo.organization_id LEFT JOIN fantech_imes.smt_work_order_card_pool swocp ON qqc.work_order_card_pool_id = swocp.work_order_card_pool_id LEFT JOIN fantech_imes.smt_work_order swo ON swo.work_order_id = swocp.work_order_id LEFT JOIN fantech_imes.base_parts_information bpi ON bpi.parts_information_id = sm.material_id LEFT JOIN fantech_imes.smt_work_order_card_pool sswocp ON sswocp.work_order_card_pool_id = swocp.parent_id LEFT JOIN fantech_imes.smt_work_order sswo ON sswo.work_order_id = sswocp.work_order_id LEFT JOIN fantech_imes.smt_material sm ON sswo.material_id = sm.material_id LEFT JOIN fantech_imes.smt_process sp ON qqc.process_id = sp.process_id LEFT JOIN fantech_imes.smt_workshop_section sws ON sws.section_id = sp.section_id LEFT JOIN fantech_imes.base_tab bt ON bt.tab_id = sm.tab_id LEFT JOIN fantech_imes.smt_product_model spm ON spm.product_model_id = bt.product_model_id LEFT JOIN fantech_imes.smt_dept sd ON sd.dept_id = su.dept_id
and qqc.quality_confirmation_id = #{qualityConfirmationId}
and qqc.quality_confirmation_code like CONCAT('%', #{qualityConfirmationCode}, '%')
and swocp.work_order_card_id like CONCAT('%', #{workOrderCardPoolCode}, '%')
and sswocp.work_order_card_id = #{parentWorkOrderCardPoolCode}
and swo.work_order_code like CONCAT('%', #{workOrderCode}, '%')
and qqc.affirm_status = #{affirmStatus}
and qqc.quality_type = #{qualityType}
and date_format(qpi.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by create_time desc
