mapper.select:
select
,
bs.supplier_name,
csu.user_name as create_user_name,
msu.user_name as modified_user_name
from srm_ht_plan_delivery_order spdo
left join base_supplier bs on bs.supplier_id = spdo.supplier_id
left join sys_user csu on csu.user_id = spdo.create_user_id
left join sys_user msu on msu.user_id = spdo.modified_user_id


    and spdo.supplier_id = #{supplierId} and spdo.order_status = 2


    and spdo.plan_delivery_order_code like CONCAT('%', #{planDeliveryOrderCode}, '%')


    and spdo.order_status = #{orderStatus}


    and bs.supplier_name like CONCAT('%', #{supplierName}, '%')


    and spdo.status = #{status}


    and spdo.is_delete = #{isDelete}


    and spdo.org_id = #{orgId}


    and spdo.create_user_id = #{createUserId}


    and spdo.modified_user_id = #{modifiedUserId}


    and spdo.remark like CONCAT('%', #{remark}, '%')


    and date_format(spdo.modified_time, '%Y-%m-%d') between #{modifiedTimeStart} and #{modifiedTimeEnd}


    and date_format(spdo.create_time, '%Y-%m-%d') between #{startTime} and #{endTime}


order by spdo.create_time desc
