mapper.select:
select , csu.user_name as create_user_name, msu.user_name as modified_user_name, opo.purchase_order_code, bs.supplier_name, opo.order_status, opo.order_date, bd.dept_name as purchase_dept_name, mosu.user_name as makeOrder_user_name, opo.make_order_date, opo.remark as order_remark from srm_ht_po_expedite shpe left join om_purchase_order opo on opo.purchase_order_id = shpe.purchase_order_id left join base_supplier bs on bs.supplier_id = shpe.supplier_id left join sys_user csu on csu.user_id = shpe.create_user_id left join sys_user msu on msu.user_id = shpe.modified_user_id left join base_dept bd on bd.dept_id = opo.purchase_dept_id left join sys_user mosu on mosu.user_id = opo.make_order_user_id
and shpe.supplier_id = #{supplierId}
and opo.purchase_order_code like CONCAT('%', #{purchaseOrderCode}, '%')
and opo.order_status = #{orderStatus}
and bs.supplier_name like CONCAT('%', #{supplierName}, '%')
and bd.dept_name like CONCAT('%', #{purchaseDeptName}, '%')
and mosu.user_name like CONCAT('%', #{makeOrderUserName}, '%')
and opo.remark like CONCAT('%', #{orderRemark}, '%')
and shpe.org_id = #{orgId}
and shpe.create_user_id = #{createUserId}
and shpe.modified_user_id = #{modifiedUserId}
and shpe.remark like CONCAT('%', #{remark}, '%')
and date_format(shpe.order_date, '%Y-%m-%d') between #{orderDateStart} and #{orderDateEnd}
and date_format(shpe.make_order_date, '%Y-%m-%d') between #{makeOrderDateStart} and #{makeOrderDateEnd}
and date_format(shpe.modified_time, '%Y-%m-%d') between #{modifiedTimeStart} and #{modifiedTimeEnd}
and date_format(shpe.create_time, '%Y-%m-%d') between #{startTime} and #{endTime}
order by shpe.create_time
