mapper.select:
select ,
csu.user_name as create_user_name,
msu.user_name as modified_user_name,
opo.purchase_order_code,
bs.supplier_name,
bm.material_code,
bm.material_name,
bm.material_version,
bm.material_desc
from srm_ht_po_production_info shppi
left join om_purchase_order opo on opo.purchase_order_id = shppi.purchase_order_id
left join base_material bm on bm.material_id = shppi.material_id
left join base_supplier bs on bs.supplier_id = opo.supplier_id
left join base_supplier_re_user bsru on bsru.supplier_id = opo.supplier_id
left join sys_user csu on csu.user_id = shppi.create_user_id
left join sys_user msu on msu.user_id = shppi.modified_user_id


    and bsru.user_id = #{userId}


    and shppi.supplier_id = #{supplierId}


    and opo.purchase_order_code like CONCAT('%', #{purchaseOrderCode}, '%')


    and shppi.work_order_code like CONCAT('%', #{workOrderCode}, '%')


    and bs.supplier_name like CONCAT('%', #{supplierName}, '%')


    and bm.material_code like CONCAT('%', #{materialCode}, '%')


    and bm.material_version like CONCAT('%', #{materialVersion}, '%')


    and bm.material_desc like CONCAT('%', #{materialDesc}, '%')


    and shppi.pro_name like CONCAT('%', #{proName}, '%')


    and shppi.work_order_qty = #{workOrderQty}


    and shppi.output_qty = #{outputQty}



    and shppi.work_order_status = #{workOrderStatus}


    and shppi.org_id = #{orgId}


    and shppi.create_user_id = #{createUserId}


    and shppi.modified_user_id = #{modifiedUserId}


    and shppi.status = #{status}


    and shppi.remark like CONCAT('%', #{remark}, '%')


    and shppi.is_delete = #{isDelete}


    and date_format(shppi.actual_start_time, '%Y-%m-%d') between #{actualStartTimeStart} and #{actualStartTimeEnd}


    and date_format(shppi.actual_end_time, '%Y-%m-%d') between #{actualEndTimeStart} and #{actualEndTimeEnd}


    and date_format(shppi.modified_time, '%Y-%m-%d') between #{modifiedTimeStart} and #{modifiedTimeEnd}


    and date_format(shppi.create_time, '%Y-%m-%d') between #{startTime} and #{endTime}


group by shppi.po_production_info_id
order by shppi.create_time
