mapper.select:
select , opo.purchase_order_code,bm.material_code,bm.material_name,bw.warehouse_name, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name AS organization_Name FROM wms_in_asn_order_det_barcode wiaodb LEFT JOIN om_purchase_order opo ON wiaod.source_order_id = opo.purchase_order_id LEFT JOIN base_material bm ON wiaodb.material_id = bm.material_id LEFT JOIN sys_user su ON wiaodb.create_user_id = su.user_id LEFT JOIN sys_user sus ON wiaodb.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wiaodb.org_id = bo.organization_id
and wiaodb.asn_order_id=#{asnOrderId}
and wiaodb.asn_order_det_id = #{asnOrderDetId}
and bm.material_code like CONCAT('%',#{materialCode},'%')
