mapper.select:
select , bs.supplier_name as supplier_name, bs.supplier_code as supplier_code, u.user_name as create_user_name, s.user_name as modified_user_name,bo.organization_name from srm_packing_order spo left join base_supplier bs on spo.supplier_id=bs.supplier_id left join ocean_v2.sys_user u on spo.create_user_id=u.user_id left join ocean_v2.sys_user s on spo.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON spo.org_id = bo.organization_id
and date_format(spo.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and bs.supplier_name like CONCAT('%', #{supplierName}, '%')
and spo.packing_order_code = #{packingOrderCode}
and spo.despatch_batch = #{despatchBatch}
and spo.order_status = #{orderStatus}
and SUBSTRING(spo.order_time,1,10) = SUBSTRING(#{orderTime},1,10)
and SUBSTRING(spo.leave_factory_time,1,10) = SUBSTRING(#{leaveFactoryTime},1,10)
and SUBSTRING(spo.leave_port_time,1,10) = SUBSTRING(#{leavePortTime},1,10)
and SUBSTRING(spo.arrival_port_time,1,10) = SUBSTRING(#{arrivalPortTime},1,10)
and SUBSTRING(spo.arrival_time,1,10) = SUBSTRING(#{arrivalTime},1,10)
and spo.status = #{status}
and spo.org_id=#{orgId}
order by spo.create_time desc
