mapper.select:
select
  ,
  bs.supplier_name,
  csu.user_name as create_user_name,
  msu.user_name as modified_user_name
from srm_plan_delivery_order spdo
left join base_supplier bs on bs.supplier_id = spdo.supplier_id
left join sys_user csu on csu.user_id = spdo.create_user_id
left join sys_user msu on msu.user_id = spdo.modified_user_id


    and spdo.supplier_id = #{supplierId} and spdo.order_status = 2


    ${query.planDeliveryOrderCode.joinQuery} spdo.plan_delivery_order_code ${query.planDeliveryOrderCode.sql}


    ${query.orderStatus.joinQuery} spdo.order_status ${query.orderStatus.sql}


    ${query.supplierName.joinQuery} bs.supplier_name ${query.supplierName.sql}


    ${query.status.joinQuery} spdo.status ${query.status.sql}


    ${query.isDelete.joinQuery} spdo.is_delete ${query.isDelete.sql}


    and spdo.org_id = #{orgId}


    ${query.createUserId.joinQuery} spdo.create_user_id ${query.createUserId.sql}


    ${query.modifiedUserId.joinQuery} spdo.modified_user_id ${query.modifiedUserId.sql}


    ${query.remark.joinQuery} spdo.remark ${query.remark.sql}


    ${query.modifiedTime.joinQuery} date_format(spdo.modified_time, '%Y-%m-%d') ${query.modifiedTime.sql}


    ${query.createTime.joinQuery} date_format(spdo.create_time, '%Y-%m-%d') ${query.createTime.sql}


order by spdo.create_time desc
