mapper.select:
select , csu.user_name as create_user_name, msu.user_name as modified_user_name, opo.purchase_order_code, bs.supplier_name, bs.supplier_code, bs.e_mail, bs.mobile_phone, bs.telephone, ba.complete_detail, opo.order_status, opo.order_date, bd.dept_name as purchase_dept_name, csu.nick_name as make_order_user_name, opo.create_time as make_order_date, opo.remark as order_remark from srm_po_expedite spe left join om_purchase_order opo on opo.purchase_order_id = spe.purchase_order_id left join base_supplier bs on bs.supplier_id = spe.supplier_id left join sys_user csu on csu.user_id = spe.create_user_id left join sys_user msu on msu.user_id = spe.modified_user_id left join base_dept bd on bd.dept_id = opo.purchase_dept_id left join base_address ba on ba.address_id = bs.address_id
and spe.supplier_id = #{supplierId}
and opo.purchase_order_code like CONCAT('%', #{purchaseOrderCode}, '%')
and opo.order_status = #{orderStatus}
and bs.supplier_name like CONCAT('%', #{supplierName}, '%')
and bd.dept_name like CONCAT('%', #{purchaseDeptName}, '%')
and mosu.user_name like CONCAT('%', #{makeOrderUserName}, '%')
and opo.remark like CONCAT('%', #{orderRemark}, '%')
and spe.org_id = #{orgId}
and spe.create_user_id = #{createUserId}
and spe.modified_user_id = #{modifiedUserId}
and spe.remark like CONCAT('%', #{remark}, '%')
and date_format(opo.order_date, '%Y-%m-%d') between #{orderDateStart} and #{orderDateEnd}
and date_format(opo.create_time, '%Y-%m-%d') between #{makeOrderDateStart} and #{makeOrderDateEnd}
and date_format(spe.modified_time, '%Y-%m-%d') between #{modifiedTimeStart} and #{modifiedTimeEnd}
and date_format(spe.create_time, '%Y-%m-%d') between #{startTime} and #{endTime}
and #FULL_FROM_QUERY
order by spe.create_time desc
