mapper.select:
select ,
    csu.user_name as create_user_name,
    msu.user_name as modified_user_name,
    opo.purchase_order_code,
    bs.supplier_name,
    bm.material_code,
    bm.material_name,
    bm.material_version,
    bm.material_desc,
    opod.order_qty
from srm_po_production_info sppi
left join om_purchase_order opo on opo.purchase_order_id = sppi.purchase_order_id
left join base_material bm on bm.material_id = sppi.material_id
left join om_purchase_order_det opod on opod.purchase_order_id = opo.purchase_order_id and opod.material_id = sppi.material_id
left join base_supplier bs on bs.supplier_id = opo.supplier_id
left join base_supplier_re_user bsru on bsru.supplier_id = opo.supplier_id
left join sys_user csu on csu.user_id = sppi.create_user_id
left join sys_user msu on msu.user_id = sppi.modified_user_id


    and bsru.user_id = #{userId}


    and sppi.supplier_id = #{supplierId}


    and opo.purchase_order_code like CONCAT('%', #{purchaseOrderCode}, '%')


    and sppi.work_order_code like CONCAT('%', #{workOrderCode}, '%')


    and bs.supplier_name like CONCAT('%', #{supplierName}, '%')


    and bm.material_code like CONCAT('%', #{materialCode}, '%')


    and bm.material_version like CONCAT('%', #{materialVersion}, '%')


    and bm.material_desc like CONCAT('%', #{materialDesc}, '%')


    and bm.material_name like CONCAT('%', #{materialName}, '%')


    and sppi.work_order_qty = #{workOrderQty}


    and sppi.output_qty = #{outputQty}



    and sppi.work_order_status = #{workOrderStatus}


    and sppi.org_id = #{orgId}


    and sppi.create_user_id = #{createUserId}


    and sppi.modified_user_id = #{modifiedUserId}


    and sppi.status = #{status}


    and sppi.remark like CONCAT('%', #{remark}, '%')


    and sppi.is_delete = #{isDelete}


    and date_format(sppi.actual_start_time, '%Y-%m-%d') between #{actualStartTimeStart} and #{actualStartTimeEnd}


    and date_format(sppi.actual_end_time, '%Y-%m-%d') between #{actualEndTimeStart} and #{actualEndTimeEnd}


    and date_format(sppi.modified_time, '%Y-%m-%d') between #{modifiedTimeStart} and #{modifiedTimeEnd}


    and date_format(sppi.create_time, '%Y-%m-%d') between #{startTime} and #{endTime}



    and #FULL_FROM_QUERY


group by sppi.po_production_info_id
order by sppi.create_time desc
