mapper.select:
SELECT , su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_code AS organization_code, sm.material_code, sm.material_name, sm.material_desc, pm.product_model_code AS product_model_code, pm.product_model_name AS product_model_name, pm.product_model_desc AS product_model_desc, bt.main_unit As main_unit, s.storage_name AS storage_name, wa.warehouse_area_name AS warehouse_area_name, w.warehouse_name AS warehouse_name, d.dept_name AS dept_name FROM wms_in_finished_product_det AS fpd LEFT JOIN ocean.sys_user su ON fpd.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON fpd.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON fpd.organization_id = bo.organization_id LEFT JOIN smt_material sm ON fpd.material_id = sm.material_id LEFT JOIN base_tab bt ON bt.material_id =sm.material_id LEFT JOIN smt_product_model pm ON bt.product_model_id = pm.product_model_id LEFT JOIN smt_storage s ON fpd.storage_id = s.storage_id LEFT JOIN smt_warehouse_area wa ON s.warehouse_area_id = wa.warehouse_area_id LEFT JOIN smt_warehouse w ON s.warehouse_id = w.warehouse_id LEFT JOIN smt_dept d ON fpd.dept_id = d.dept_id
and date_format(fp.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and fpd.finished_product_id = #{finishedProductId}
