mapper.select:
SELECT, bm.material_name as materialName,bm.material_code as materialCode,bm.material_desc as materialDesc,bm.material_version as materialVersion, ibs.storage_name as inStorageName,obs.storage_name as outStorageName,ibw.warehouse_name as inWarehouseName,obw.warehouse_name as outWarehouseName, u.user_name AS create_user_name, s.user_name AS modified_user_name, bo.organization_name FROM wms_inner_direct_transfer_order_det widtod LEFT JOIN base_material bm ON widtod.material_id=bm.material_id LEFT JOIN base_storage ibs ON widtod.in_storage_id=ibs.storage_id LEFT JOIN base_storage obs ON widtod.out_storage_id=obs.storage_id LEFT JOIN base_warehouse ibw ON ibs.warehouse_id=ibw.warehouse_id LEFT JOIN base_warehouse obw ON obs.warehouse_id=obw.warehouse_id LEFT JOIN sys_user u ON widtod.create_user_id=u.user_id LEFT JOIN sys_user s ON widtod.modified_user_id=s.user_id LEFT JOIN base_organization bo ON widtod.org_id = bo.organization_id
and widtod.org_id = #{orgId}
and widtod.direct_transfer_order_id = #{directTransferOrderId}
order by widtod.create_time desc
