mapper.select:
select , su.user_name AS create_user_name, sus.user_name AS modified_user_name, suss.user_name AS processor_user_name, bo.organization_name,bo.organization_code from fantech_imes.wms_inner_ht_transfer_slip wihts LEFT JOIN ocean.sys_user su ON wihts.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON wihts.modified_user_id = sus.user_id LEFT JOIN ocean.sys_user suss ON wihts.processor_user_id = suss.user_id LEFT JOIN fantech_imes.base_organization bo ON wihts.organization_id = bo.organization_id
and date_format(wihts.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and wihts.transfer_slip_code like CONCAT("%",#{transferSlipCode},"%")
and sus.user_name like CONCAT("%",#{modifiedUserName},"%")
and suss.user_name like CONCAT("%",#{processorUserName},"%")
and wihts.status=#{status}
and wihts.transfer_slip_status=#{transferSlipStatus}
and wihts.order_type=#{orderType}
order by wihts.create_time desc
