mapper.select:
SELECT ,bs.storage_code,bw.warehouse_code,bw.warehouse_name, IFNULL((SELECT sum(wiisd.stock_qty) FROM wms_inner_init_stock_det wiisd WHERE wiisd.init_stock_id=wiis.init_stock_id),0) as totalStockQty, CASE wiis.init_stock_type WHEN 1 THEN wiis.total_plan_qty ELSE IFNULL((SELECT sum(wiisd.plan_qty) FROM wms_inner_init_stock_det wiisd WHERE wiisd.init_stock_id=wiis.init_stock_id),0) END as totalPlanQty, CASE wiis.init_stock_type WHEN 1 THEN wiis.total_plan_qty-IFNULL((SELECT sum(wiisd.stock_qty) FROM wms_inner_init_stock_det wiisd WHERE wiisd.init_stock_id=wiis.init_stock_id),0) ELSE IFNULL((SELECT sum(wiisd.variance_qty) FROM wms_inner_init_stock_det wiisd WHERE wiisd.init_stock_id=wiis.init_stock_id),0) END as totalVarianceQty, su.user_name as 'createUserName',sus.user_name as 'modifiedUserName',bo.organization_name as 'organizationName' FROM wms_inner_init_stock wiis LEFT JOIN base_storage bs ON wiis.storage_id=bs.storage_id LEFT JOIN base_warehouse bw ON bs.warehouse_id = bw.warehouse_id LEFT JOIN ocean_v2.sys_user su ON wiis.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON wiis.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wiis.org_id = bo.organization_id
and wiis.org_id = #{orgId}
and bw.warehouse_id=#{warehouseId}
and bs.storage_id = #{storageId}
and bs.storage_code like CONCAT('%',#{storageCode},'%')
and bw.warehouse_name like CONCAT('%',#{warehouseName},'%')
and wiis.init_stock_type=#{initStockType}
and wiis.init_stock_order_code like CONCAT('%',#{initStockOrderCode},'%')
and wiis.order_status=#{orderStatus}
and wiis.order_status in
#{orderStatus}
order by wiis.create_time desc
