mapper.select:
select,
bs.storage_code,bs.storage_type,bs.warehouse_id,bw.warehouse_name,bo.organization_name,bis.inventory_status_name,
wimb.barcode as barcode,wimb.color_box_code as colorBoxCode,wimb.carton_code as cartonCode,wimb.pallet_code as palletCode,
wimb.batch_code as batchCode,wimb.production_time as productionTime,wimb.material_qty as materialQty,wimb.material_id as materialId,wimb.barcode_type as barcodeType,bm.material_code as material_code,
bm.material_name as material_name,bps.package_specification_quantity,
su.user_name AS create_user_name, sus.user_name AS modified_user_name
from wms_inner_inventory_det wiid
LEFT JOIN base_inventory_status bis ON bis.inventory_status_id = wiid.inventory_status_id
LEFT JOIN base_storage bs ON wiid.storage_id = bs.storage_id
LEFT JOIN base_warehouse bw ON bs.warehouse_id = bw.warehouse_id
LEFT JOIN wms_inner_material_barcode wimb ON wiid.material_barcode_id = wimb.material_barcode_id
LEFT JOIN base_material bm ON wimb.material_id = bm.material_id
LEFT JOIN base_material_tab bmt ON bm.material_id = bmt.material_id
LEFT JOIN base_package_specification bps ON bmt.package_specification_id = bps.package_specification_id
LEFT JOIN sys_user su ON wiid.create_user_id = su.user_id
LEFT JOIN sys_user sus ON wiid.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON wiid.org_id = bo.organization_id


        and bw.warehouse_name like CONCAT('%',#{warehouseName},'%')


        and wimb.barcode = #{barcode}


        and wimb.color_box_code = #{colorBoxCode}


        and wimb.carton_code = #{cartonCode}


        and wimb.pallet_code = #{palletCode}


        and wiid.storage_id = #{storageId}


        and wiid.storage_id != #{storageId}


        and bm.material_id != #{materialId}


        and date_format(wiid.production_date, '%Y-%m-%d') BETWEEN #{productionStartDate} AND #{productionEndDate}


        and date_format(wiid.expired_date, '%Y-%m-%d') BETWEEN #{expiredStartDate} AND #{expiredEndDate}


        and date_format(wiid.receiving_date, '%Y-%m-%d') BETWEEN #{receivingStartDate} AND #{receivingEndDate}


        and date_format(wiid.deliver_date, '%Y-%m-%d') BETWEEN #{deliverStartDate} AND #{deliverEndDate}


        and wiid.inventory_status_id = #{inventoryStatusId}


        and wiid.inspection_order_code like CONCAT('%',#{inspectionOrderCode},'%')


        and wiid.inspection_order_code is null


        and bs.storage_code = #{storageCode}


        and bm.material_id = #{materialId}


        and bm.material_code like CONCAT('%',#{materialCode},'%')


        and bm.material_name like CONCAT('%',#{materialName},'%')


        and wiid.if_stock_lock = #{ifStockLock}


        and wiid.asn_code like CONCAT('%',#{asnCode},'%')


        and bss.supplier_name like CONCAT('%',#{supplierName},'%')


        and su.user_name like CONCAT('%',#{createUserName},'%')


        and sus.user_name like CONCAT('%',#{modifiedUserName},'%')


        and wiid.barcode_status = #{barcodeStatus}


        and date_format(wiid.qc_date, '%Y-%m-%d')=#{qcDate}


        and wiid.delivery_order_code like CONCAT('%',#{deliveryOrderCode},'%')


        and wiid.org_id = #{orgId}


        and wiid.material_barcode_id = #{materialBarcodeId}



        and wimb.material_barcode_id

            #{item}



order by wiid.create_time desc

update.foreach:
update wms_inner_inventory_det wiid


        wiid.storage_id = #{item.storageId}


        wiid.barcode_status = #{item.barcodeStatus}


where wiid.inventory_det_id = #{item.inventoryDetId}
