mapper.select:
SELECT, bmo.material_owner_name, bw.warehouse_name, bso.storage_code AS out_storage_name, bso.storage_code AS out_storage_code, bsi.storage_code AS in_storage_name, bsi.storage_code AS in_storage_code, bp.platform_name, bm.material_code, bm.material_name, bm.material_desc, bis.inventory_status_name, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name AS organization_name, bpl.pro_name, ws.stacking_code AS pallet_code, wijo.option3 AS sales_code, wijo.option4 AS same_package_code, rel.user_name AS release_name FROM wms_inner_job_order_det wipod LEFT JOIN wms_inner_job_order wijo ON wipod.job_order_id = wijo.job_order_id LEFT JOIN base_material_owner bmo ON wipod.material_owner_id = bmo.material_owner_id LEFT JOIN base_warehouse bw ON wipod.warehouse_id = bw.warehouse_id LEFT JOIN base_storage bso ON wipod.out_storage_id = bso.storage_id LEFT JOIN base_storage bsi ON wipod.in_storage_id = bsi.storage_id left JOIN base_platform bp on wipod.platform_id = bp.platform_id LEFT JOIN base_inventory_status bis ON wipod.inventory_status_id = bis.inventory_status_id LEFT JOIN base_material bm ON wipod.material_id = bm.material_id LEFT JOIN ocean_v2.sys_user su ON wipod.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON wipod.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wipod.org_id = bo.organization_id LEFT JOIN wms_in_asn_order_det wiaod ON wipod.source_det_id = wiaod.asn_order_det_id LEFT JOIN mes_pm_work_order mpwo ON wiaod.source_order_id = mpwo.work_order_id AND wijo.order_type_id=4 LEFT JOIN base_pro_line bpl ON mpwo.pro_line_id = bpl.pro_line_id LEFT JOIN wms_inner_job_order_re_mspp wijorm ON wijo.job_order_id = wijorm.job_order_id LEFT JOIN wanbao_stacking ws ON wijorm.product_pallet_id = ws.stacking_id LEFT JOIN ocean_v2.sys_user rel ON rel.user_id = wijo.release_user_id
LEFT JOIN base_working_area_re_w bwarw ON bsi.working_area_id = bwarw.working_area_id LEFT JOIN base_worker bwork ON bwarw.worker_id = bwork.worker_id
AND bwork.user_id = #{userId}
and wipod.job_order_id = #{jobOrderId}
and wipod.job_order_det_id = #{jobOrderDetId}
and wipod.material_id = #{materialId}
and wipod.order_status in
#{orderStatus}
and wipod.shift_storage_status != #{nonShiftStorageStatus} and bis.inventory_status_name != '不合格'
and wijo.job_order_type =#{jobOrderType}
and wipod.org_id = #{orgId}
order by wipod.create_time desc

        select ms.pallet_code FROM mes_sfc_product_pallet ms
LEFT JOIN wms_inner_job_order_re_mspp wi ON  wi.product_pallet_id = ms.product_pallet_id
where wi.job_order_id = #{jobOrderId}
