mapper.select:
SELECT, bmo.material_owner_name,boy.order_type_name,bw.warehouse_name,wr.user_name as work_name, wodo.consignee as consignee_name,wodo.link_man_name,wodo.link_man_phone,wodo.fax_number,wodo.e_mail_address,wodo.detailed_address, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name AS organization_Name, bp.platform_name, ws.stacking_code, (SELECT IFNULL(SUM(wipod.distribution_qty),0) FROM wms_inner_job_order_det wipod where wipod.job_order_id = wipo.job_order_id) as distribution_qty, (SELECT IFNULL(SUM(wipod.actual_qty),0) FROM wms_inner_job_order_det wipod where wipod.job_order_id = wipo.job_order_id) as actual_qty
, CASE WHEN (select COUNT(*) FROM wms_inner_job_order_re_mspp wijorm where wijorm.job_order_id=wipo.job_order_id)>0 THEN 1 ELSE 0 END as isPallet
FROM wms_inner_job_order wipo LEFT JOIN wms_out_delivery_order wodo ON wipo.source_order_id = wodo.delivery_order_id LEFT JOIN base_material_owner bmo ON wipo.material_owner_id=bmo.material_owner_id LEFT JOIN base_warehouse bw ON wipo.warehouse_id = bw.warehouse_id LEFT join base_order_type boy on wipo.order_type_id = boy.order_type_id LEFT JOIN base_worker bwr ON wipo.worker_id = bwr.worker_id left JOIN base_platform bp on wipo.platform_id = bp.platform_id LEFT JOIN ocean_v2.sys_user wr ON bwr.user_id = wr.user_id LEFT JOIN ocean_v2.sys_user su ON wipo.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON wipo.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wipo.org_id = bo.organization_id LEFT JOIN wms_inner_job_order_re_mspp wijorm ON wipo.job_order_id = wijorm.job_order_id LEFT JOIN wanbao_stacking ws ON wijorm.product_pallet_id = ws.stacking_id
and wipo.org_id = #{orgId}
and bwr.user_id = #{userId}
and wipo.option1 = #{option1}
and wipo.job_order_code like CONCAT('%',#{jobOrderCode},'%')
and bmo.material_owner_name like CONCAT('%',#{materialOwnerName},'%')
and bw.warehouse_name like CONCAT('%',#{warehouseName},'%')
and wipo.related_order_code like CONCAT('%',#{relatedOrderCode},'%')
and wipo.related_order_code = #{relatedOrderCode}
and wipo.order_type_id =#{orderTypeId}
and wr.user_name =#{workName}
and wipo.job_order_type =#{jobOrderType}
and wipo.job_order_id = #{jobOrderId}
and wipo.source_order_id =#{sourceOrderId}
and wipo.order_status =#{orderStatus}
and wipo.remark like CONCAT('%',#{remark},'%')
and wipo.job_order_type =4 and wipo.order_status = 5 and wipo.job_order_id NOT IN(SELECT wodor.job_order_id From wms_out_despatch_order_re_jo wodor where wodor.job_order_id=wipo.job_order_id)
and wipo.order_status in
#{orderStatus}
and DATE_FORMAT(wipo.work_start_time,'%y-%m-%d')<=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 21 day),'%y-%m-%d') and wipo.order_status!=6 and wipo.order_type_id=8
order by wipo.create_time desc

SELECT ,bw.warehouse_name
FROM wms_inner_job_order wipo
LEFT JOIN base_warehouse bw ON wipo.warehouse_id = bw.warehouse_id


        and wipo.org_id = #{orgId}


        and wipo.job_order_code like CONCAT('%',#{jobOrderCode},'%')


        and wipo.job_order_id = #{jobOrderId}


        and wipo.source_order_id =#{sourceOrderId}


        and wipo.job_order_type = #{jobOrderType}

    and wipo.is_delete = 1 and (wipo.order_status = 3 or wipo.order_status = 4)

order by wipo.create_time desc
