mapper.select:
SELECT, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name AS organization_Name, bm.material_name as materialName, bm.material_code as materialCode, bs.supplier_name as supplierName FROM wms_inner_material_barcode wimb LEFT JOIN sys_user su ON wimb.create_user_id = su.user_id LEFT JOIN sys_user sus ON wimb.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wimb.org_id = bo.organization_id LEFT JOIN base_material bm on bm.material_id = wimb.material_id LEFT JOIN base_supplier bs on bs.supplier_id = wimb.supplier_id
and wimb.org_id = #{orgId}
and wimb.material_barcode_id = #{materialBarcodeId}
and wimb.barcode like CONCAT('%',#{barcode},'%')
and wimb.print_order_type_code = #{printOrderTypeCode}
and wimb.if_sys_barcode = #{ifSysBarcode}
and wimb.color_box_code like CONCAT('%',#{colorBoxCode},'%')
and wimb.carton_code like CONCAT('%',#{cartonCode},'%')
and wimb.pallet_code like CONCAT('%',#{palletCode},'%')
and bm.material_code like CONCAT('%',#{materialCode},'%')
and bm.material_name like CONCAT('%',#{materialName},'%')
and date_format(wimb.production_time, '%Y-%m-%d') BETWEEN #{productionTimeStart} AND #{productionTimeEnd}
and wimb.print_order_det_id = #{printOrderDetId}
and wimb.print_order_code like CONCAT('%',#{printOrderCode},'%')
and wimb.material_id = #{materialId}
and wimb.supplier_id = #{supplierId}
and wimb.material_barcode_id
#{item}
order by wimb.create_time desc

update.foreach:
update wms_inner_material_barcode wimb set
wimb.inspection_status = #{item.inspectionStatus}
where wimb.material_barcode_id = #{item.materialBarcodeId}

mapper.select:
SELECT br.barcode_rule as 'barcodeRule',br.barcode_rule_id as 'barcodeRuleId',lc.barcode_type as 'barcodeType',
bl.label_code as 'labelCode', bl.label_name as 'labelName',bl.label_version as 'labelVersion',bl.print_mode as 'printMode',
lm.once_print_count as 'oncePrintQty'
FROM base_label_category lc
LEFT JOIN base_barcode_rule br ON lc.label_category_id = br.barcode_rule_category_id
LEFT JOIN base_label bl ON lc.label_category_id = bl.label_category_id
LEFT JOIN base_label_material lm ON lm.label_id = bl.label_id  AND lm.material_id =#{materialId}


        and lc.label_category_code = #{code}


        and br.org_id=#{orgId}

SELECT sum(wimb.material_qty)
FROM wms_inner_material_barcode wimb
LEFT JOIN base_material bm on bm.material_id = wimb.material_id


        and wimb.org_id = #{orgId}


        and wimb.print_order_type_code = #{printOrderTypeCode}


        and wimb.print_order_det_id = #{printOrderDetId}



        and wimb.material_id = #{materialId}
