mapper.select:
SELECT,
su.user_name AS create_user_name,
sus.user_name AS modified_user_name,
bo.organization_name AS organization_Name,
bm.material_name,
bm.material_code,
wimb.material_qty as qty,wimb.production_time as production_date,wimb.create_type,wimb.inspection_status,
wimb.color_box_code,wimb.carton_code,wimb.pallet_code,wimb.batch_code,wimb.barcode
FROM wms_inner_material_barcode_re_order wimbro
LEFT JOIN sys_user su ON wimbro.create_user_id = su.user_id
LEFT JOIN sys_user sus ON wimbro.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON wimbro.org_id = bo.organization_id
left join wms_inner_material_barcode wimb on wimb.material_barcode_id = wimbro.material_barcode_id
LEFT JOIN base_material bm on bm.material_id = wimb.material_id


        and wimb.if_sys_barcode = #{ifSysBarcode}


        and wimbro.org_id = #{orgId}


        and wimbro.order_type_code = #{orderTypeCode}


        and wimbro.order_id = #{orderId}



        and wimbro.order_det_id = #{orderDetId}



            #{item}




        and wimbro.material_barcode_id = #{materialBarcodeId}


        and wimbro.scan_status = #{scanStatus}


        and wimbro.order_code = #{orderCode}


        and wimb.barcode = #{barcode}


        and wimb.color_box_code = #{colorBoxCode}


        and wimb.carton_code = #{cartonCode}


        and wimb.pallet_code = #{palletCode}


        and wimb.barcode like CONCAT('%',#{barcode},'%')


        and wimb.color_box_code like CONCAT('%',#{colorBoxCode},'%')


        and wimb.carton_code like CONCAT('%',#{cartonCode},'%')


        and wimb.pallet_code like CONCAT('%',#{palletCode},'%')


        and date_format(wimb.production_time, '%Y-%m-%d') BETWEEN date_format(#{productionTimeStart}, '%Y-%m-%d') AND  date_format(#{productionTimeEnd}, '%Y-%m-%d')


        and date_format(wimb.production_time, '%Y-%m-%d') BETWEEN date_format(#{productionDate}, '%Y-%m-%d') AND  date_format(#{productionDate}, '%Y-%m-%d')


        and wimb.material_id = #{materialId}


        and bm.material_code like CONCAT('%',#{materialCode},'%')


        and bm.material_name like CONCAT('%',#{materialName},'%')


        and wimb.batch_code like CONCAT('%',#{batchCode},'%')


        and wimb.inspection_status = #{inspectionStatus}


order by wimbro.create_time desc
