mapper.select:
SELECT ,bmo.material_owner_name,sw.warehouse_name,su.user_name AS create_user_name, sus.user_name AS modified_user_name,bo.organization_name FROM wms_inner_stock_order wiv LEFT JOIN base_material_owner bmo ON wiv.material_owner_id=bmo.material_owner_id LEFT JOIN smt_warehouse sw ON wiv.warehouse_id = sw.warehouse_id LEFT JOIN ocean.sys_user su ON wiv.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON wiv.modified_user_id = sus.user_id LEFT JOIN fantech_imes.base_organization bo ON wiv.organization_id = bo.organization_id
and wiv.inventory_verification_code like CONCAT('%',#{inventoryVerificationCode},'%')
and sw.warehouseName like CONCAT('%',#{warehouseName},'%')
and wiv.related_order_code like CONCAT('%',#{relatedOrderCode},'%')
and wiv.inventory_verification_type = #{inventoryVerificationType}
and wiv.project_type = #{projectType}
and wiv.status = #{status}
and date_format(wiv.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and su.user_name like CONCAT('%',#{createUserName},'%')
and wiv.order_status =
order by wiv.create_time desc
