mapper.select:
select , su.user_name AS create_user_name, sus.user_name AS modified_user_name, suss.user_name AS processor_user_name, bo.organization_name,bo.organization_code from fantech_imes.wms_inner_transfer_slip wits LEFT JOIN ocean.sys_user su ON wits.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON wits.modified_user_id = sus.user_id LEFT JOIN ocean.sys_user suss ON wits.processor_user_id = suss.user_id LEFT JOIN fantech_imes.base_organization bo ON wits.organization_id = bo.organization_id
and date_format(wits.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and wits.transfer_slip_code like CONCAT("%",#{transferSlipCode},"%")
and sus.user_name like CONCAT("%",#{modifiedUserName},"%")
and suss.user_name like CONCAT("%",#{processorUserName},"%")
and wits.status=#{status}
and wits.transfer_slip_status=#{transferSlipStatus}
and wits.transfer_slip_status = 0 or wits.transfer_slip_status = 1
order by wits.create_time desc
