mapper.select:
SELECT , wodro.delivery_req_order_code, bm.material_code,bm.material_name, bm.material_desc,bm.material_version, sudelivery.user_name as delivery_user_name, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name AS organization_Name FROM wms_out_delivery_req_order_det wodrod LEFT JOIN base_material bm ON wodrod.material_id = bm.material_id LEFT JOIN wms_out_delivery_req_order wodro ON wodrod.delivery_req_order_id = wodro.delivery_req_order_id LEFT JOIN sys_user sudelivery ON wodrod.delivery_user_id = sudelivery.user_id LEFT JOIN sys_user su ON wodrod.create_user_id = su.user_id LEFT JOIN sys_user sus ON wodrod.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wodrod.org_id = bo.organization_id
and wodrod.delivery_req_order_id = #{deliveryReqOrderId}
and wodrod.org_id = #{orgId}
and wodrod.status = #{status}
and date_format(wodrod.create_time, '%Y-%m-%d') >= #{startTime} and date_format(wodrod.create_time, '%Y-%m-%d') <= #{endTime}
order by wodrod.create_time desc

update.foreach:
update wms_out_delivery_req_order_det wodrod set
wodrod.total_issue_qty = #{item.totalIssueQty},
wodrod.if_all_issued = #{item.ifAllIssued}
where wodrod.delivery_req_order_det_id = #{item.deliveryReqOrderDetId}
