mapper.select:
SELECT, su.user_name AS create_user_name, sus.user_name AS modified_user_name, suss.user_name AS warehouse_user_name, woo.otherout_code, sm.material_code, sm.material_name, sm.material_desc, sm.unit, sm.version, sw.warehouse_name, sw.warehouse_code, sw.warehouse_desc, bo.organization_name FROM wms_out_otherout_det wood LEFT JOIN ocean.sys_user su ON wood.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON wood.modified_user_id = sus.user_id LEFT JOIN ocean.sys_user suss ON wood.warehouse_user_id = suss.user_id LEFT JOIN wms_out_otherout woo ON wood.otherout_id = woo.otherout_id LEFT JOIN smt_material sm ON wood.material_id = sm.material_id LEFT JOIN smt_warehouse sw ON wood.warehouse_id = sw.warehouse_id LEFT JOIN fantech_imes.base_organization bo ON wood.organization_id = bo.organization_id
and date_format(wood.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and woo.otherout_code like CONCAT('%', #{otheroutCode}, '%')
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sw.warehouse_code like CONCAT('%', #{warehouseCode}, '%')
and sw.warehouse_name like CONCAT('%', #{warehouseName}, '%')
and sw.warehouse_desc like CONCAT('%', #{warehouseDesc}, '%')
and wood.otherout_Id=#{otheroutId}
and wood.status=#{status}
order by wood.create_time desc
