mapper.select:
select, mpdp.daily_plan_code, mpwo.work_order_code, wopslo.plan_stock_list_order_code, wopslo.warehouse_id, wopslo.core_source_sys_order_type_code, smp.material_code AS material_code_product, smp.material_name AS material_name_product, sm.material_code, sm.material_name, sm.material_desc, sm.material_version, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name FROM wms_out_plan_stock_list_order_det wopslod LEFT JOIN wms_out_plan_stock_list_order wopslo ON wopslod.plan_stock_list_order_id=wopslo.plan_stock_list_order_id LEFT JOIN mes_pm_daily_plan_stock_list mpdplist ON wopslod.source_id=mpdplist.daily_plan_stock_list_id LEFT JOIN mes_pm_daily_plan_det mpdpd ON mpdplist.daily_plan_det_id=mpdpd.daily_plan_det_id LEFT JOIN mes_pm_daily_plan mpdp ON mpdpd.daily_plan_id=mpdp.daily_plan_id LEFT JOIN mes_pm_work_order mpwo ON mpdpd.work_order_id = mpwo.work_order_id LEFT JOIN base_material sm on wopslod.material_id=sm.material_id LEFT JOIN base_material smp on mpwo.material_id=smp.material_id LEFT JOIN sys_user su ON wopslod.create_user_id = su.user_id LEFT JOIN sys_user sus ON wopslod.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wopslod.org_id = bo.organization_id
and wopslod.plan_stock_list_order_det_id = #{planStockListOrderDetId}
and wopslod.core_source_order_code = #{coreSourceOrderCode}
and wopslod.source_order_code = #{sourceOrderCode}
and wopslod.core_source_id = #{coreSourceId}
and wopslod.source_id = #{sourceId}
and wopslod.plan_stock_list_order_id = #{planStockListOrderId}
and wopslod.material_id = #{materialId}
and wopslod.line_status = #{lineStatus}
and wopslod.status=#{status}
and wopslod.remark =#{remark}
and wopslod.org_id = #{orgId}
order by wopslod.create_time desc

update.foreach:
update wms_out_plan_stock_list_order_det wodrod set
wodrod.total_issue_qty = #{item.totalIssueQty},
wodrod.if_all_issued = #{item.ifAllIssued}
where wodrod.plan_stock_list_order_det_id = #{item.planStockListOrderDetId}
