mapper.select:
select, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name AS organization_name, sm.material_code, sm.material_name, sm.material_desc,sm.version, w.warehouse_name AS warehouse_name, wa.warehouse_area_name AS warehouse_area_name, s.storage_name AS storage_name, ww.warehouse_name AS move_warehouse_name, waw.warehouse_area_name AS move_warehouse_area_name, st.storage_name AS move_storage_name from wms_out_shipping_note_det AS snd LEFT JOIN ocean.sys_user su ON snd.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON snd.modified_user_id = sus.user_id LEFT JOIN fantech_imes.base_organization bo ON snd.organization_id = bo.organization_id LEFT JOIN smt_storage s ON snd.storage_id = s.storage_id LEFT JOIN smt_warehouse w ON s.warehouse_id = w.warehouse_id LEFT JOIN smt_warehouse_area wa ON s.warehouse_area_id = wa.warehouse_area_id LEFT JOIN smt_storage st ON snd.move_storage_id = st.storage_id LEFT JOIN smt_warehouse ww ON st.warehouse_id = ww.warehouse_id LEFT JOIN smt_warehouse_area waw ON st.warehouse_area_id = waw.warehouse_area_id LEFT JOIN smt_material sm ON snd.material_id = sm.material_id
and date_format(sn.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and snd.shipping_note_id = #{shippingNoteId}
