mapper.select:
select su.user_name
from ocean.sys_user su
where su.user_id = #{id}

SELECT mpm.package_manager_id, mpm.organization_id, mpm.package_manager_code, mpm.bar_code, mpm.work_order_id, mpm.material_id, mpm.total, mpm.packing_unit_id, mpm.package_specification_id, mpm.parent_id, mpm.is_delete, mpm.remark, mpm.create_user_id, mpm.create_time, mpm.modified_user_id, mpm.modified_time, su1.user_name AS 'createUserName', su2.user_name AS 'modifiedUserName', bo.organization_name AS 'organizationName' swo.work_order_code AS 'workOrderCode', sm.material_code AS 'materialCode', sm.material_desc AS 'materialDesc', spl.pro_name AS 'proName', sps.package_specification_quantity AS 'packageSpecificationQuantity' FROM mes_package_manager AS mpm LEFT JOIN ocean.sys_user su1 ON su1.user_id = mpm.create_user_id LEFT JOIN ocean.sys_user su2 ON su2.user_id = mpm.modified_user_id LEFT JOIN base_organization bo ON mpm.organization_id = bo.organization_id LEFT JOIN smt_work_order swo ON swo.work_order_id = mpm.work_order_id LEFT JOIN smt_material sm ON sm.material_id = mpm.material_id LEFT JOIN smt_pro_line spl ON spl.pro_line_id = swo.pro_line_id LEFT JOIN smt_package_specification sps ON sps.package_specification_id = mpm.package_specification_id
AND swo.work_order_code = #{workOrderCode}
AND mpm.create_time = #{startTime} AND mpm.modified_time = #{endTime}
AND mpm.bar_code = #{barcode}
AND mpm.package_manager_code = #{packageManagerCode}
AND mpm.parent_id = #{parentId}
AND mpm.is_delete=1
order by mpm.modified_time desc
