SELECT bo.organization_name, mpwo.work_order_code, oso.sales_order_code, bws.work_shop_name, bpl.pro_name, bw.warehouse_name, bwa.warehouse_area_code, bs.storage_code, bm.material_code, bm.material_name, sum(wijod.actual_qty) as 'qty', wiaod.packing_unit_name FROM wms_inner_job_order_det wijod LEFT JOIN wms_inner_job_order wijo ON wijod.job_order_id = wijo.job_order_id LEFT JOIN wms_in_asn_order_det wiaod ON wiaod.asn_order_det_id = wijod.source_det_id LEFT JOIN wms_in_asn_order wiao ON wiaod.asn_order_id = wiao.asn_order_id LEFT JOIN mes_pm_work_order mpwo ON wiaod.source_order_id = mpwo.work_order_id LEFT JOIN base_pro_line bpl ON mpwo.pro_line_id = bpl.pro_line_id left JOIN base_work_shop bws ON bpl.work_shop_id = bws.work_shop_id left JOIN om_sales_order oso ON mpwo.sales_order_id = oso.sales_order_id LEFT JOIN base_warehouse bw ON wijod.warehouse_id = bw.warehouse_id LEFT JOIN base_storage bs ON wijod.in_storage_id = bs.storage_id LEFT JOIN base_warehouse_area bwa ON bs.warehouse_area_id = bwa.warehouse_area_id LEFT JOIN base_material bm ON wijod.material_id = bm.material_id LEFT JOIN fantech_imes_v2.base_organization bo ON wijod.org_id = bo.organization_id
wiao.order_type_id = 4 AND wijo.order_type_id=4 AND wijo.order_status=5
and bw.warehouse_id =#{warehouseId}
and bm.material_id = #{materialId}
and bpl.pro_name LIKE CONCAT('%',#{proName},'%')
and bws.work_shop_name LIKE CONCAT('%',#{workShopName},'%')
and bo.organization_id =#{orgId}
and bws.work_shop_id =#{workShopId}
and bpl.pro_line_id =#{proId}
and mpwo.work_order_id = #{workOrderId}
and oso.sales_order_id = #{salesOrderId}
and oso.sales_order_code like CONCAT('%',#{salesOrderCode},'%')
and mpwo.work_order_code like CONCAT('%',#{workOrderCode},'%')
and date_format(wijod.work_end_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and bwa.warehouse_area_code like CONCAT('%',#{warehouseAreaCode},'%')
and bs.storage_code like CONCAT('%',#{storageCode},'%')
and bm.material_code like CONCAT('%',#{materialCode},'%')
and bm.material_name like CONCAT('%',#{materialName},'%')
and wiaod.packing_unit_name like CONCAT('%',#{packingUnitName},'%')
GROUP BY bo.organization_name, mpwo.work_order_code, oso.sales_order_code, bws.work_shop_name, bpl.pro_name, bw.warehouse_name, bwa.warehouse_area_code, bs.storage_code, bm.material_code, bm.material_name, wiaod.packing_unit_name

SELECT (@i:=@i+1) 'serialNumber', bo.organization_name, mpwo.work_order_code, oso.sales_order_code, bws.work_shop_name, bpl.pro_name, bw.warehouse_name, bwa.warehouse_area_code, bs.storage_code, bm.material_code, bm.material_name, wiid.material_qty as 'qty', wiaod.packing_unit_name, wiid.barcode 'inPlantBarCode', mskpr.part_barcode 'marketBarCode', mspcd.create_time as 'packageTime', msppd.create_time as 'putInStorageTime' FROM wms_inner_job_order_det wijod LEFT JOIN wms_inner_job_order wijo ON wijod.job_order_id = wijo.job_order_id LEFT JOIN wms_in_asn_order_det wiaod ON wiaod.asn_order_det_id = wijod.source_det_id LEFT JOIN wms_in_asn_order wiao ON wiaod.asn_order_id = wiao.asn_order_id left JOIN wms_inner_inventory_det wiid ON wiao.asn_code = wiid.asn_code AND wiid.material_id = wijod.material_id LEFT JOIN mes_sfc_work_order_barcode mswob ON wiid.barcode = mswob.barcode LEFT JOIN mes_sfc_key_part_relevance mskpr ON mswob.work_order_barcode_id = mskpr.work_order_barcode_id AND mskpr.part_barcode IS NOT NULL LEFT JOIN mes_sfc_product_carton_det mspcd ON mswob.work_order_barcode_id = mspcd.work_order_barcode_id LEFT JOIN mes_sfc_product_pallet_det msppd ON mswob.work_order_barcode_id = msppd.work_order_barcode_id LEFT JOIN mes_pm_work_order mpwo ON wiaod.source_order_id = mpwo.work_order_id LEFT JOIN base_pro_line bpl ON mpwo.pro_line_id = bpl.pro_line_id left JOIN base_work_shop bws ON bpl.work_shop_id = bws.work_shop_id left JOIN om_sales_order oso ON mpwo.sales_order_id = oso.sales_order_id LEFT JOIN base_warehouse bw ON wijod.warehouse_id = bw.warehouse_id LEFT JOIN base_storage bs ON wijod.in_storage_id = bs.storage_id LEFT JOIN base_warehouse_area bwa ON bs.warehouse_area_id = bwa.warehouse_area_id LEFT JOIN base_material bm ON wijod.material_id = bm.material_id LEFT JOIN fantech_imes_v2.base_organization bo ON wijod.org_id = bo.organization_id,(SELECT @i:=0)j
wiao.order_type_id = 4 AND wijo.order_type_id=4 AND wijod.order_status=5 AND wiid.barcode IS NOT NULL
and bw.warehouse_id =#{warehouseId}
and bm.material_id = #{materialId}
and bo.organization_id =#{orgId}
and bws.work_shop_id =#{workShopId}
and bpl.pro_line_id =#{proId}
and mpwo.work_order_id = #{workOrderId}
and oso.sales_order_id = #{salesOrderId}
and oso.sales_order_code like CONCAT('%',#{salesOrderCode},'%')
and mpwo.work_order_code like CONCAT('%',#{workOrderCode},'%')
and date_format(wijod.work_end_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
GROUP BY wiid.barcode

SELECT msbp.material_code, msbp.material_name, mpwo.sales_code, bws.work_shop_name, bpl.pro_name, case when t.id = 1 then 1 when t.id = 2 then 2 when t.id = 3 then 3 else 4 end AS site, COUNT(msbp.barcode_process_id) AS qty, case when t.id = 1 then IF ( msbp.devote_time IS NOT NULL, '0', '1' ) when t.id = 2 then IF ( msbp.production_time IS NOT NULL, '0', '1' ) else IF ( wiid.create_time IS NOT NULL, '0', '1' ) end AS pass_status, case when t.id = 1 then IF ( msbp.devote_time IS NOT NULL, msbp.devote_time, NULL ) when t.id = 2 then IF ( msbp.production_time IS NOT NULL, msbp.production_time, NULL ) else IF ( wiid.create_time IS NOT NULL, wiid.create_time, NULL ) end AS time FROM mes_sfc_barcode_process msbp LEFT JOIN base_pro_line bpl ON msbp.pro_line_id = bpl.pro_line_id LEFT JOIN base_work_shop bws ON bpl.work_shop_id = bws.work_shop_id LEFT JOIN mes_pm_work_order mpwo ON msbp.work_order_id = mpwo.work_order_id LEFT JOIN wms_inner_inventory_det wiid ON wiid.barcode = msbp.barcode LEFT JOIN ( SELECT 1 AS id, '包箱' AS 'name' UNION SELECT 2 AS id, '栈板' AS 'name' UNION SELECT 3 AS id, '入库' AS 'name') AS t ON 1 = 1
and bws.work_shop_name = #{workShopName}
and bpl.pro_name = #{proName}
AND t.id = #{site}
AND (case when t.id = 1 then msbp.devote_time IS NOT NULL when t.id = 2 then msbp.production_time IS NOT NULL else wiid.create_time IS NOT NULL end)
AND (case when t.id = 1 then msbp.devote_time IS NULL when t.id = 2 then msbp.production_time IS NULL else wiid.create_time IS NULL end)
AND (date_format(msbp.devote_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime} or date_format(msbp.production_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime} or date_format(wiid.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime})
GROUP BY t.id, msbp.material_code, mpwo.sales_code, case when t.id = 1 then IF ( msbp.devote_time IS NOT NULL, '100', '000' ) when t.id = 2 then IF ( msbp.production_time IS NOT NULL, '110', '100' ) else IF ( wiid.create_time IS NOT NULL, '111', '110' ) end

SELECT msbp.material_code, msbp.material_name, mpwo.sales_code, bws.work_shop_name, bpl.pro_name, case when d.id = 1 then 1 when d.id = 2 then 2 when d.id = 3 then 3 else 4 end AS site, case when d.id = 1 then IF ( msbp.devote_time IS NOT NULL, '0', '1' ) when d.id = 2 then IF ( msbp.production_time IS NOT NULL, '0', '1' ) else IF ( wiid.create_time IS NOT NULL, '0', '1' ) end AS pass_status, msbp.barcode, mskpr.part_barcode AS 'cutsomer_barcode', mskpr2.part_barcode AS 'sales_barcode', '1' AS qty, IF (wiid.inventory_det_id IS NOT NULL, wiid.create_time, IF (msbp.production_time IS NOT NULL,msbp.production_time, IF (msbp.devote_time IS NOT NULL,msbp.devote_time,NULL))) AS time FROM mes_sfc_barcode_process msbp LEFT JOIN base_pro_line bpl ON msbp.pro_line_id = bpl.pro_line_id LEFT JOIN base_work_shop bws ON bpl.work_shop_id = bws.work_shop_id LEFT JOIN mes_pm_work_order mpwo ON msbp.work_order_id = mpwo.work_order_id LEFT JOIN mes_sfc_key_part_relevance mskpr ON msbp.work_order_barcode_id = mskpr.work_order_barcode_id AND mskpr.material_id IS NOT NULL LEFT JOIN mes_sfc_key_part_relevance mskpr2 ON msbp.work_order_barcode_id = mskpr2.work_order_barcode_id AND mskpr2.label_category_id IS NOT NULL LEFT JOIN wms_inner_inventory_det wiid ON wiid.barcode = msbp.barcode LEFT join ( select 1 as id,'包箱' as 'name' UNION select 2 as id,'栈板' as 'name' UNION select 3 as id,'入库' as 'name' ) d on 1= 1
and bws.work_shop_name = #{workShopName}
and bpl.pro_name = #{proName}
AND d.id = #{site}
AND (case when d.id = 1 then msbp.devote_time IS NOT NULL when d.id = 2 then msbp.production_time IS NOT NULL else wiid.create_time IS NOT NULL end)
AND (case when d.id = 1 then msbp.devote_time IS NULL when d.id = 2 then msbp.production_time IS NULL else wiid.create_time IS NULL end)
AND (date_format(msbp.devote_time, '%Y-%m-%d %H:%i:%s') BETWEEN #{startTime} AND #{endTime} or date_format(msbp.production_time, '%Y-%m-%d %H:%i:%s') BETWEEN #{startTime} AND #{endTime} or date_format(wiid.create_time, '%Y-%m-%d %H:%i:%s') BETWEEN #{startTime} AND #{endTime})
