mapper.select:
select , biw.inspection_way_desc,bis.inspection_standard_name s.user_name as modified_user_name,bo.organization_name from qms_ipqc_inspection_order qiio left join base_inspection_way biw on qiio.inspection_way_id=biw.inspection_way_id left join base_inspection_standard bis on qiio.inspection_standard_id=bis.inspection_standard_id left join ocean_v2.sys_user su on qiio.audit_user_id=su.user_id left join ocean_v2.sys_user u on qiio.create_user_id=u.user_id left join ocean_v2.sys_user s on qiio.modified_user_id=s.user_id left join base_organization bo ON qiio.org_id = bo.organization_id
and qiio.ipqc_inspection_order_code like CONCAT('%', #{ipqcInspectionOrderCode}, '%')
and biw.inspection_way_code =#{inspectionWayCode}
and qiio.org_id=#{orgId}
order by qiio.create_time desc
